return (
  <React.Fragment>
    <section>
      <button onClick={fetchMoviesHandler}>Fetch Movies</button>
    </section>
    <section>
      {!isLoading && movies.length > 0 && <MoviesList movies={movies} />}
      {isLoading && <p>Loading...</p>}
      {!isLoading && movies.length === 0 && <p>Found no movies ;( </p>}
    </section>
  </React.Fragment>
);
}

export default App;
